var React = require("react-native");
import PropTypes from "prop-types";
var {
  StyleSheet,
  Component,
  View,
  Text,
  Image,
  ScrollView,
  TouchableWithoutFeedback,
  NativeModules: { UIManager }
} = React;

var window = require("Dimensions").get("window");

var arrow = {
  uri:
    "data:image/png;base64,",
  isStatic: true,
  width: 15,
  height: 15
};

var styles = StyleSheet.create({
  container: {
    height: 40,
    borderColor: "#BDBDC1",
    borderWidth: 2 / window.scale
  },
  select: {
    position: "absolute",
    borderColor: "#BDBDC1",
    borderWidth: 2 / window.scale,
    left: -1
  },
  option: {
    padding: 10
  }
});

class DropDown extends Component {
  constructor(props) {
    super(props);

    var selectedValue;
    if (props.options && props.options.length > props.defaultOption) {
      selectedValue = props.defaultOption;
    }

    this.contentOffset = { x: 0, y: 0 };

    this.state = {
      open: false,
      top: 0,
      width: 0,
      selectedValue
    };
  }

  _toggleDropDownMenu() {
    var selectDisplay = this.refs["select_display"];
    var { open } = this.state;
    var handle;

    if (open) {
      this.setState({
        ...this.state,
        open: false
      });
    } else {
      handle = React.findNodeHandle(selectDisplay);
      UIManager.measure(handle, (x, y, width, height, pageX, pageY) => {
        this.setState({
          ...this.state,

          open: true,
          top: height,
          width: width + 2
        });
      });
    }
  }

  _onSelect(index) {
    var { options, onSelect } = this.props;
    var option = options[index];
    var { selectedValue } = this.state;

    this.setState({
      ...this.state,
      selectedValue: index,
      open: false
    });

    if (onSelect && selectedValue !== index) {
      onSelect(index);
    }
  }

  renderOption(option) {
    return (
      <View style={styles.option}>
        <Text>{option.label}</Text>
      </View>
    );
  }

  _renderOptions() {
    var { options } = this.props;

    return options.map((option, index) => (
      <TouchableWithoutFeedback
        key={index}
        onPress={this._onSelect.bind(this, index)}
      >
        {this.renderOption(option)}
      </TouchableWithoutFeedback>
    ));
  }

  _onScroll(event) {
    var {
      nativeEvent: { contentOffset }
    } = event;

    this.contentOffset = { ...contentOffset };
  }

  _renderPopupWindow() {
    var { open, top, width } = this.state;
    var { options, optionHeight, numberOptionsDisplay } = this.props;

    var height = 10;

    if (numberOptionsDisplay <= options.length) {
      height = optionHeight * (numberOptionsDisplay - 1) + optionHeight / 2;
    } else if (options.length > 0) {
      height = optionHeight * options.length;
    }

    return !open ? null : (
      <View style={[styles.select, { top: top, width: width, height: height }]}>
        <ScrollView
          scrollEventThrottle={1}
          onScroll={this._onScroll.bind(this)}
          contentOffset={this.contentOffset}
          automaticallyAdjustContentInsets={false}
          bounces={false}
        >
          {this._renderOptions()}
        </ScrollView>
      </View>
    );
  }

  getSelectedItem() {
    const { selectedValue } = this.state;
    return selectedValue;
  }

  render() {
    var { options } = this.props;
    var { selectedValue } = this.state;

    var option = options[selectedValue];

    if (!option) {
      option = {};
    }

    return (
      <View style={styles.container}>
        <TouchableWithoutFeedback onPress={this._toggleDropDownMenu.bind(this)}>
          <View
            ref="select_display"
            style={{ flex: 1, flexDirection: "row", alignItems: "center" }}
          >
            <View style={{ flex: 1, paddingLeft: 10, paddingRight: 10 }}>
              <Text>{option.label}</Text>
            </View>
            <View style={{ width: 25, alignItems: "center" }}>
              <Image source={arrow} />
            </View>
          </View>
        </TouchableWithoutFeedback>
        {this._renderPopupWindow()}
      </View>
    );
  }
}

DropDown.propTypes = {
  defaultOption: PropTypes.number,
  options: PropTypes.arrayOf(
    PropTypes.shape({
      label: PropTypes.string.isRequired
    })
  ),
  onSelect: PropTypes.func,
  optionHeight: PropTypes.number,
  numberOptionsDisplay: PropTypes.number
};

DropDown.defaultProps = {
  defaultOption: -1,
  options: [],
  optionHeight: 38,
  numberOptionsDisplay: 3
};

module.exports = DropDown;
